import React, { useRef, useState } from 'react';
import './Video.css'

function Video() {
    const [playing, setPlaying] = useState(false);
    const videoRef = useRef(null);

    const handleVideoPress = () => {
        // If video is playing stop it
        // otherwise play it
        if (playing) {
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);
        }
    };

    return (
        <div className="video">
            <video onclick={handleVideoPress} className="video__player" loop ref={videoRef} src=""></video>

            {/* Video footer */}

            {/* Video side bar */}
        </div>
    )
}

export default Video
